#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Tue Feb  6 11:44:38 2024@author: JWW"""from PIL import Imagedef save_montage(directory, imgs):    frames = []    for i in range(52):        path = directory + '/frames/img_%i.png' %i        frames.append(Image.open(path))    frames[0].save(directory + '/montage.gif', save_all=True, append_images=frames[1:], duration = 250, loop = 0, optimize = False, quality=100)